<?php function wp_isell_reset_logfile(){    $log_reset = true;    $logfile = dirname(__FILE__).'/wp_isell_debug.log';    $text = '['.date('m/d/Y g:i A').'] - SUCCESS : Log file reset';    $text .= "\n------------------------------------------------------------------\n\n";    $fp = fopen($logfile, 'w');    if($fp != FALSE) {            @fwrite($fp, $text);            @fclose($fp);    }    else{            $log_reset = false;	    }    return $log_reset;}function wp_isell_write_debug($message,$success,$end=false,$debug_log_file_name=''){    $debug_enabled = false;    $isell_options = isell_get_options();	    if(isset($isell_options['advanced']['wp_isell_enable_debug']) && $isell_options['advanced']['wp_isell_enable_debug'] == '1'){	        $debug_enabled = true;    }    if (!$debug_enabled) return;    // Timestamp    $text = '['.date('m/d/Y g:i A').'] - '.(($success)?'SUCCESS :':'FAILURE :').$message. "\n";    if ($end) {    	$text .= "\n------------------------------------------------------------------\n\n";    }        if(empty($debug_log_file_name)){        $debug_log_file_name = iSell_Path.'wp_isell_debug.log';    }    // Write to log    $fp=fopen($debug_log_file_name,'a');    fwrite($fp, $text );    fclose($fp);  // close file}function wp_isell_write_debug_array($array_to_write,$success,$end=false,$debug_log_file_name=''){    $debug_enabled = false;    $isell_options = isell_get_options();	    if(isset($isell_options['advanced']['wp_isell_enable_debug']) && $isell_options['advanced']['wp_isell_enable_debug'] == '1'){	        $debug_enabled = true;    }    if (!$debug_enabled) return;    // Timestamp    $text = '['.date('m/d/Y g:i A').'] - '.(($success)?'SUCCESS :':'FAILURE :'). "\n";    ob_start();     print_r($array_to_write);     $var = ob_get_contents();     ob_end_clean();         $text .= $var;        if ($end){    	$text .= "\n------------------------------------------------------------------\n\n";    }    if(empty($debug_log_file_name)){        $debug_log_file_name = iSell_Path.'wp_isell_debug.log';    }        // Write to log    $fp=fopen($debug_log_file_name,'a');    fwrite($fp, $text );    fclose($fp);  // close file}